function tabHandler(t){let e=this.querySelectorAll(".tab-item"),a=this.nextElementSibling.querySelectorAll(".pane-item");t.target.classList.contains("is-active")||t.target==this||e.forEach((e,i)=>{e==t.target?(t.target.classList.add("is-active"),a[i].classList.add("show")):(e.classList.remove("is-active"),a[i].classList.remove("show"))})}document.addEventListener("DOMContentLoaded",()=>{lazyload(),AOS.init({duration:700,anchorPlacement:"center-bottom"}),document.querySelectorAll(".tab-list").forEach(t=>{t.addEventListener("click",tabHandler)})});